rule.definition:
with(
  local!isComponentReadOnly: if(
    isnull(
      ri!currentcomponent.isReadOnly
    ),
    false(),
    ri!currentcomponent.isReadOnly
  ),
  local!isComponentRequired: if(
    isnull(
      ri!currentcomponent.isRequired
    ),
    false(),
    ri!currentcomponent.isRequired
  ),
  local!componentDefaultValue: fn!index(
    ri!currentComponent,
    "defaultValue",
    {}
  ),
  local!isComponentWithDefaultNull: if(
    and(
      local!isComponentReadOnly,
      local!isComponentRequired,
      isnull(
        local!componentDefaultValue
      )
    ),
    true(),
    false()
  ),
  local!validations:{
    if(
      local!isComponentWithDefaultNull,
      "Component "&ri!currentComponent.name & " has its default value null.",
      null()
    ),
    if(
      contains(
        #"_a-0000da69-8640-8000-6ee0-010000010000_1148"(),
        ri!currentComponent.responseType
      ),
      #"b440b7a9-d018-4200-bba5-c45ca87d3b2a"(
        ri!currentComponent
      ),
      null()
    ),
    if(
      isnull(
        ri!currentComponent.parentList
      ),
      null(),
      if(
        any(
          fn!isnull,
          ri!currentComponent.parentList.parentComponentId
        ),
        "Component "&ri!currentComponent.name & " has null value for its parent component",
        null()
      )
    ),
    if(isnull(ri!currentComponent.name),
    "Component name cannot be null",
    null()
    )

  },
  /*local!validations*/
  /*count(local!validations)*/
  /*length(local!validations)*/
  local!validations
)
