    and(
      local!shouldDisplayComponent,
      local!component_cdt.responseType = #"_a-0000da69-8640-8000-6ee0-010000010000_1157"
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: local!componentLabel,
      firstColumnContents: {
        #"SYSTEM_SYSRULES_applyComponents"(
          function: #"20f5e2e6-421d-42ca-9fc1-510228e2a309"(
            componentIndex: _,
            formData_cdt: ri!formData_cdt,
            readOnly: ri!readOnly,
            fileUploadDocList: ri!fileUploadDocList
          ),
          array: local!firstColumnComponentIndexListForSection
        )
      },
      secondColumnContents: {
        #"SYSTEM_SYSRULES_applyComponents"(
          function: #"20f5e2e6-421d-42ca-9fc1-510228e2a309"(
            componentIndex: _,
            formData_cdt: ri!formData_cdt,
            readOnly: ri!readOnly,
            fileUploadDocList: ri!fileUploadDocList
          ),
          array: local!secondColumnComponentIndexListForSection
        )
      }
    ),
    null
  ),
  /* for some unknown reason, grid response is not working, so this variable is not used*/
  local!componentListForGrid: if(
    fn!and(
      local!shouldDisplayComponent,
      local!component_cdt.responseType = #"_a-0000dad3-098b-8000-927a-010000010000_71855"
    ),
    fn!filter(
      #"_a-0000dad3-098b-8000-927a-010000010000_71860"(
        component_cdt: _,
