      local!ImageFieldResponse,
      local!richTextBoxResponse,
      local!userPickerResponse,
      local!documentPickerResponse,
      local!fileUploadResponse,
      local!milestoneResonse
    }
  ),
  if(
    length(
      local!returnSAILComponent
    ) = 0,
    /*a!textField(*/
    /*  readOnly: true*/
    /*),*/
    {},
    local!returnSAILComponent[1]
  )
)
