rule.definition:
with(
  #"SYSTEM_SYSRULES_sectionLayout"(
    label: if(
      isnull(
        ri!cdtAppForms.Id
      ),
      "Create New Application",
      "Edit Application"
    ),
    firstColumnContents: {
      #"SYSTEM_SYSRULES_textField"(
        label: "Name",
        labelPosition: "ADJACENT",
        instructions: "Enter application name",
        value: ri!cdtAppForms.name,
        saveInto: {
          a!save(
            ri!cdtAppForms.name,
            cleanwith(trim(
              save!value
            ),#"_a-0000dcbf-080b-8000-927a-010000010000_206873"
          )
          )
        },
        refreshAfter: "UNFOCUS",
        required: true,
        requiredmessage: "Please enter application name",
        validations: {
          /*if(
            fn!regexmatch(
              "^[A-Za-z0-9_:\s-]+$",
              ri!cdtAppForms.name
            ),
            "",
            "Please enter a valid application name"
          ),*/
          if(
            fn!len(
              ri!cdtAppForms.name
            ) <= 200,
            "",
            "The character limit is 200"
          )
        }
      ),
      #"SYSTEM_SYSRULES_pickerFieldUsers"(
        label: "Select Users",
        labelPosition: "ADJACENT",
        instructions: "Users who can create new instances of this application",
        value: ri!usrAppUsers,
        saveInto: ri!usrAppUsers,
        refreshAfter: "UNFOCUS",
        groupFilter: #"_a-0000dc91-0813-8000-927a-010000010000_201900",
        /*required: true,*/
        requiredmessage: "Please select application users",
        validations: {}
      ),
      #"SYSTEM_SYSRULES_paragraphField"(
        label: "Description",
        labelPosition: "ADJACENT",
        instructions: "Enter application description",
        value: ri!cdtAppForms.description,
        saveInto: {
          a!save(
            ri!cdtAppForms.description,
            #"3a8ab5b3-edae-4a9e-b8f0-b9cf4ed59f70"(
              save!value
            )
          )
        },
        refreshAfter: "UNFOCUS",
        required: true,
        requiredmessage: "Please enter application description",
        validations: {}
      ),
      if(
        ri!intFormCount = #"_a-0000dc82-66ce-8000-927a-010000010000_200869",
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: #"SYSTEM_SYSRULES_richTextItem"(
            text: "Maximum of " & #"_a-0000dc82-66ce-8000-927a-010000010000_200869" & " can be created.",
            style: "EMPHASIS"
          )
        ),
        #"SYSTEM_SYSRULES_linkField"(
          labelPosition: "ADJACENT",
          links: {
            #"SYSTEM_SYSRULES_dynamicLink"(
              label: "Click here to add tasks",
              value: local!CurrentMaxIndex + 1,
              saveInto: {
                a!save(
                  ri!txtButton,
                  null
                ),
                a!save(
                  ri!cdtAppForms.Forms,
                  append(
                    ri!cdtAppForms.Forms,
                    #"5e071303-8da7-4900-8e89-2eeac8699508"(
                      save!value
                    )
                  )
                ),
                a!save(
                  ri!cdtFrmVersionCmpnt,
                  append(
                    ri!cdtFrmVersionCmpnt,
                    #"_a-0000dc7e-93eb-8000-927a-010000010000_200046"(
                      intIndex: save!value,
                      intVersion: 1
                    )
                  )
                ),
                a!save(
                  ri!FormIndex,
                  ri!intformCount + 1
                ),
                a!save(
                  value: local!CurrentMaxIndex,
                  save!value
                ),
                a!save(
                  ri!intFormCount,
                  ri!intformCount + 1
                )
              }
            )
          }
        )
      ),
      if(
        or(
          isnull(
            ri!cdtFrmVersionCmpnt
          ),
          count(
            ri!cdtFrmVersionCmpnt
          ) = 0
        ),
        {},
        {
          #"4577c967-319f-42b2-a978-fe23b5a69b09"(
            cdtFormVersionComponent: ri!cdtFrmVersionCmpnt,
            cdtForms: ri!cdtAppForms.Forms,
            FormIndex: ri!FormIndex,
            txtButton: ri!txtButton,
            intFormCount: ri!intFormCount
          )
        }
      ),
      #"SYSTEM_SYSRULES_ButtonLayout"(
        primaryButtons: {
          #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
            label: if(
              isnull(
                ri!cdtAppForms.Id
              ),
              index(
                #"_a-0000dc7c-e2e3-8000-927a-010000010000_199989",
                1,
                ""
              ),
              index(
                #"_a-0000dc7c-e2e3-8000-927a-010000010000_199989",
                3,
                ""
              )
            ),
            value: if(
              isnull(
                ri!cdtAppForms.Id
              ),
              index(
                #"_a-0000dc7e-d5cf-8000-927a-010000010000_200308",
                1,
                ""
              ),
              index(
                #"_a-0000dc7e-d5cf-8000-927a-010000010000_200308",
                3,
                ""
              )
            ),
            disabled: or(
              isnull(
                ri!cdtAppForms.Forms
              ),
              count(
                ri!cdtAppForms.Forms
              ) = 0
            ),
            style: "PRIMARY",
            saveInto: ri!txtButton
          )
        },
        secondaryButtons: #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
          label: index(
            #"_a-0000dc7c-e2e3-8000-927a-010000010000_199989",
            2,
            ""
          ),
          value: index(
            #"_a-0000dc7e-d5cf-8000-927a-010000010000_200308",
            2,
            ""
          ),
          style: "NORMAL",
          skipValidation: true(),
          saveInto: ri!txtButton
        )
      )
    },
    secondColumnContents: {}
  )
)
