rule.definition:
load(
  local!ShowAppInstanceDetails: {},
  local!SelectedStatus: "",

  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex : 1,
    batchSize : 5,
    sort : #"SYSTEM_SYSRULES_sortInfo"(
      field : "CreationDate",
      ascending : false()
    )
  ),
  with(
    local!AppInstanceIds: #"8fe7a9b1-fb87-4fca-bc17-643d5c757014"(
      ri!intAppId
    ).Id,
    local!arrcdtAppInstance: #"c119f87b-e10f-4969-bd31-47d8c81bec83"(
      local!AppInstanceIds
    ),
    local!AppInstanceStatus: touniformString(
      index(
        local!arrcdtAppInstance,
        "Status",
        {}
      )
    ),
    local!AppInstanceId: tointeger(
      index(
        local!arrcdtAppInstance,
        "AppInstanceId",
        {}
      )
    ),
    local!UniqueInstanceStatus: union(
      local!AppInstanceStatus,
      local!AppInstanceStatus
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Application Instance Details - " & ri!txtApplicationName,
      firstColumnContents : {
        #"SYSTEM_SYSRULES_pieChartField"(
        label: "Application Instances",
        labelPosition: "COLLAPSED",
        showTooltips: true(),
        series: {
          apply(
            #"e2615ffa-c6d2-46fe-834f-36e554973ebe"(
              txtStatus: _,
              arrtxtApplicationInstanceStatus: local!AppInstanceStatus,
              arrintAppInstanceIds: local!AppInstanceId,
              arrintSelectedAppInstanceId: ri!arrintSelectedAppInstanceId,
              SelectedStatus: local!SelectedStatus
            ),
            local!UniqueInstanceStatus
          )
        }
      )  
      },
      secondColumnContents : {
         if(
       and( length(
          ri!arrintSelectedAppInstanceId
        ) > 0,     length(local!UniqueInstanceStatus)>0 ),
        with(
          local!AppInstanceGridData: #"SYSTEM_SYSRULES_queryEntity"(
            entity: #"_a-0000dc89-4db7-8000-927a-010000010000_201610",
            query: #"SYSTEM_SYSRULES_query"(
              filter: #"SYSTEM_SYSRULES_queryFilter"(
                field: "Id",
                operator: "in",
                value: ri!arrintSelectedAppInstanceId
              ),
              pagingInfo: local!pagingInfo
            )
          ),
          {
             #"SYSTEM_SYSRULES_textField"(
          readOnly : true()
        ),
          #"SYSTEM_SYSRULES_gridField"(
            label: local!SelectedStatus & " Instances",
            instructions: "Click on application instance name for more details",
            totalCount: local!AppInstanceGridData.totalCount,
            columns: {
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Name",
                field: "Name",
                data: index(
                  local!AppInstanceGridData.data,
                  "Name",
                  {}
                ),
                links: apply(
                  #"SYSTEM_SYSRULES_recordLink"(
                    recordType: #"_a-0000dcbf-080b-8000-927a-010000010000_206207",
                    Identifier: _
                  ),
                  touniformstring(
                    index(
                      local!AppInstanceGridData.data,
                      "Id",
                      {}
                    )
                  )
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Creation Date",
                field: "CreationDate",
                data: index(
                  local!AppInstanceGridData.data,
                  "CreationDate",
                  {}
                )
              )
            },
            value: local!pagingInfo,
            saveInto: local!pagingInfo
          )
          }

        ),
        #"SYSTEM_SYSRULES_textField"(
          readOnly : true()
        )
      )
      }
    )

  )
)
