rule.definition:
if(
  ri!cdtComponentList[ri!index].responseType = #"_a-0000da69-8640-8000-6ee0-010000010000_1157",
  {},
  with(
    local!CompntName: substitute(ri!cdtComponentList[ri!index].name, " ", "_"),
    local!defaultValue: ri!cdtComponentList[ri!index].defaultValue,
    local!Val: if(
      contains(
        #"_a-0000da69-8640-8000-6ee0-010000010000_1148"(),
        ri!cdtComponentList[ri!index].responseType
      ),
      if(
        fn!contains(
          fn!index(
            ri!cdtComponentList[ri!index],
            "optionList",
            "optionTxt",
            ""
          ),
          local!defaultValue
        ),
        local!defaultValue,
        ""
      ),
      local!defaultValue
    ),
    "local!" & local!CompntName & " : " & char(
      34
    ) & local!Val & char(
      34
    ) 
  )
)
