rule.definition:
with(
  local!UserGroupIds: tointeger(
    index(
      #"SYSTEM_SYSRULES_queryEntity"(
        entity: #"_a-0000dc89-4db7-8000-927a-010000010000_201624",
        query: #"SYSTEM_SYSRULES_query"(
          selection: #"SYSTEM_SYSRULES_querySelection"(
            #"SYSTEM_SYSRULES_queryColumn"( field: "ViewerGroup")
          ),
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            batchSize: - 1
          )
        )
      ).data,
      "ViewerGroup",
      ""
    )
  ),
  if(
    isusermemberofgroup(loggedInUser(),#"_a-0000dc7c-e2e3-8000-927a-010000010000_199955"),
    {local!UserGroupIds},
    if(
      isnull(local!UserGroupIds),
      {tointeger(#"_a-0000dc91-0813-8000-927a-010000010000_201900")},
      tointeger(filter(
        isusermemberofgroup(loggedInUser(),_),
        local!UserGroupIds
      ))
    )
  )
)
