rule.definition:
= load(
  local!original_cdt: ri!formData_cdt,
  local!componentIndex: 1,
  local!isChangeTheSequenceIndex: false(),
  local!currentSectionId,
  with(
    
    local!FormIndex: ri!FormIndex,
    local!componentListCount: if(
      fn!isnull(
        ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
      ),
      0,
      count(
        ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
      )
    ),
    local!validationMessage:if(
      local!componentListCount>1,
      #"0c6b969b-59f8-44ef-af76-a47841d3a92a"(
            currentComponent:ri!formData_cdt.componentList[local!componentIndex]
          ),
          {}
          ),
    local!componentListNames: if(
      local!componentListCount > 0,
      index(
        ri!formData_cdt.componentList,
        "name",
        {}
      ),
      {}
    ),
    local!duplicateComponentNames: if(
      local!componentListCount > 0,
      #"c1f1ed5f-032a-40a9-8f22-084c76b4367c"(
        local!componentListNames
      ),
      {}
    ),
    local!exitBtn: if(
      ri!isDashboardView,
      {},
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Delete Form",
        value: "Delete",
        style: "NORMAL",
        /*skipValidation: true,*/
        confirmMessage: "Your form will not be saved, do you really want to go back?",
        saveInto: {
          ri!actionButton_txt,
          a!save(
            ri!cdtFrmVersionCmpnt,
            remove(
              ri!cdtFrmVersionCmpnt,
              ri!FormIndex
            )
          ),
          a!save(
            ri!cdtForms,
            remove(
              ri!cdtForms,
              ri!FormIndex
            )
          ),
          a!save(
            local!isChangeTheSequenceIndex,
            false()
          ),
          a!save(
            ri!intFormCount,
            ri!intFormCount - 1
          )
        }
      )
    ),
    local!copyFormBtn: if(
      ri!isDashboardView,
      {},
      {}/*a!buttonWidgetSubmit(*/
      /*         label: "Copy From Other Form",*/
      /*         style: "PRIMARY",*/
      /*         value: "copyOtherForm",*/
      /*         saveInto: {*/
      /*           ri!actionButton_txt,*/
      /*           a!save(*/
      /*             ri!componentIndex,*/
      /*             ri!componentIndex*/
      /*           )*/
      /*         }*/
      /*       )*/
      
    ),
    local!deleteComponentBtn: if(
      ri!isDashboardView,
      {},
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Delete Component",
        disabled: or(
          isnull(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ),
          count(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ) = 0
        ),
        style: "NORMAL",
        submit: false,
        /*validate: true,*/
        value: ri!componentIndex,
        saveInto: {
          a!save(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList,
            apply(
              #"66d7cde1-f158-4270-8b08-6130b8ded222"(
                cdtFrmComponent: _,
                txtDeletedComponentId: ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList[ri!componentIndex].Id
              ),
              {
                ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
              }
            )
          ),
          if(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList[ri!componentIndex].responseType = #"_a-0000da69-8640-8000-6ee0-010000010000_1157",
            a!save(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList,
              apply(
                #"858be0fc-2f75-4cd1-9975-d96699062b1f"(
                  cdtFrmComponent: _,
                  txtDeletedSectionId: ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList[ri!componentIndex].Id
                ),
                {
                  ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
                }
              )
            ),
            {}
          ),
          a!save(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList,
            fn!remove(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList,
              save!value
            )
          ),
          a!save(
            local!componentIndex,
            length(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            )
          ),
          a!save(
            ri!componentIndex,
            local!componentIndex
          )
        }
      )
    ),
    local!addComponentBtn: if(
      ri!isDashboardView,
      {},
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Add Component",
        style: "NORMAL",
        submit: false,
        validate: true,
        value: #"ccf9f838-1646-4b21-a6e3-e197b579f9ba"(
          seqIndex: local!componentListCount + 1
        ),
        saveInto: {
          a!save(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList,
            fn!append(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList,
              save!value
            )
          ),
          a!save(
            local!componentIndex,
            length(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            )
          ),
          a!save(
            ri!componentIndex,
            local!componentIndex
          )
        }
      )
    ),
    local!ChangeSeqBtn: if(
      ri!isDashboardView,
      {},
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: if(
          local!isChangeTheSequenceIndex = true(),
          "Change Property",
          "Change Sequence"
        ),
        style: "NORMAL",
        submit: false,
        disabled: or(
          isnull(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ),
          count(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ) = 0
        ),
        validate: true,
        value: if(
          local!isChangeTheSequenceIndex = true(),
          false(),
          true()
        ),
        saveInto: {
          local!isChangeTheSequenceIndex
        }
      )
    ),
    local!nextBtn: if(
      local!componentIndex < local!componentListCount,
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Next",
        style: "PRIMARY",
        value: local!componentIndex + 1,
        saveInto: {
          local!componentIndex
        },
        submit: false,
        validate: true
      ),
      {}
    ),
    local!prevComponentBtn: if(
      local!componentIndex > 1,
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Previous",
        style: "PRIMARY",
        value: local!componentIndex - 1,
        saveInto: {
          local!componentIndex
        },
        submit: false,
        validate: true
      ),
      {}
    ),
    local!previewFromBtn: if(
      ri!isDashboardView,
      {},
      if(
        or(
          fn!isnull(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ),
          length(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ) = 0
        ),
        {},
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Preview",
          style: "NORMAL",
          value: "Preview",
          saveInto: {
            ri!actionButton_txt
          },
          validate: fn!true()
        )
      )
    ),
    local!basicInfoSection: #"SYSTEM_SYSRULES_sectionLayout"(
      firstColumnContents: {
        /*a!textField(
          readOnly: true(),
          label: "Last Modified By",
          required: false,
          value: ri!cdtFrmVersionCmpnt[ri!FormIndex].lastModifiedBy
        ),*/
        #"SYSTEM_SYSRULES_textField"(
          label: "Task Name",
          Instructions: "This name will not be displayed to the end user",
          required: true,
          value: ri!cdtFrmVersionCmpnt[ri!FormIndex].name,
          saveInto: {
            a!save(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].name,
              trim(
                save!value
              )
            )
          }
        )
      },
      secondColumnContents: {
        /*a!textField(
          readOnly: true(),
          label: "Last Modified Time",
          required: false,
          value: ri!cdtFrmVersionCmpnt[ri!FormIndex].lastModifiedTime
        ),*/
        #"SYSTEM_SYSRULES_textField"(
          label: "Form Label",
          Instructions: "This label is displayed to the end user",
          required: true,
          value: ri!cdtFrmVersionCmpnt[ri!FormIndex].label,
          saveInto: {
            a!save(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].label,
              trim(
                save!value
              )
            )
          }
        )
      }
    ),
    local!compDropdown: if(
      or(
        fn!isnull(
          ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
        ),
        ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList = {},
        length(
          ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
        ) = 0
      ),
      {},
      #"SYSTEM_SYSRULES_dropdownField"(
        label: "Configure Component",
        instructions: "Select component to be configured by name",
        /* placeholderLabel:"--No Components Added--",*/
        choiceLabels: ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList.name,
        choiceValues: if(
          or(
            isnull(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            ),
            count(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            ) = 0
          ),
          null,
          1 + enumerate(
            count(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            )
          )
        ),
        value: if(
          or(
            isnull(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            ),
            count(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
            ) = 0
          ),
          null,
          local!componentIndex
        ),
        saveInto: {
          a!save(
            local!componentIndex,
            if(and(length(local!validationMessage)=0,length(local!duplicateComponentNames)=0),
            save!value,
            local!componentIndex
          )
          ),
          a!save(
            ri!componentIndex,
            local!componentIndex
          )
        },
        validations :{
          local!validationMessage
          /*if(length(local!duplicateComponentNames)>0,*/
          /*local!duplicateComponentNames& " is a duplicate component name.",*/
          /*{}*/
          /*)*/
        }
      )
    ),
    local!instructionSection: #"SYSTEM_SYSRULES_sectionLayout"(
      firstColumnContents: {
        #"SYSTEM_SYSRULES_paragraphField"(
          label: "Task Description",
          Instructions: "Help documenting what this form is used for",
          /*required: true,*/
          value: ri!cdtFrmVersionCmpnt[ri!FormIndex].description,
          saveInto: {
            a!save(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].description,
              #"3a8ab5b3-edae-4a9e-b8f0-b9cf4ed59f70"(
                save!value
              )
            )
          }
        ),
        local!compDropdown
      },
      secondColumnContents: {
        #"SYSTEM_SYSRULES_textField"(
          label: "Instruction",
          Instructions: "Provide some instruction to end user",
          /*required: true,*/
          value: ri!cdtFrmVersionCmpnt[ri!FormIndex].instruction,
          saveInto: {
            a!save(
              ri!cdtFrmVersionCmpnt[ri!FormIndex].instruction,
              trim(
                save!value
              )
            )
          }
        ),
        #"SYSTEM_SYSRULES_pickerFieldGroups"(
          label: "Default assignee",
          instructions: "Default group to whom this task would be assigned",
          maxSelections: 1,
          value: ri!cdtForms[ri!FormIndex].DefaultAssigneeGrp,
          saveInto: ri!cdtForms[ri!FormIndex].DefaultAssigneeGrp,
          placeholder: "None"
        ),
        #"SYSTEM_SYSRULES_textField"(
          labelPosition: "ADJACENT",
          readonly: true()
        ),
        #"SYSTEM_SYSRULES_ButtonLayout"(
          primaryButtons: {
            local!ChangeSeqBtn
          }
        )
      }
    ),
    local!mainConfigSection: if(
      or(
        fn!isnull(
          ri!formData_cdt
        ),
        fn!isnull(
          ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
        ),
        length(
          ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
        ) = 0
      ),
      {},
      #"ac71f376-5676-40ac-9e32-8a04e81ca3e7"(
        formData_cdt: ri!formData_cdt,
        componentIndex: local!componentIndex,
        fileUploadDocList: ri!fileUploadDocList,
        isDashboardView: ri!isDashboardView,
        isChangeTheSequenceIndex: local!isChangeTheSequenceIndex,
        currentSectionId: ri!formData_cdt.componentList[ri!componentIndex].sectionComponentId,
        actionButton_txt: ri!actionButton_txt
      )
    ),
    local!saveBtn: if(
      ri!isDashboardView,
      {},
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Save",
        value: "Save",
        style: "PRIMARY",
        disabled: or(
          isnull(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ),
          count(
            ri!cdtFrmVersionCmpnt[ri!FormIndex].componentList
          ) = 0
        ),
        validate: fn!true(),
        saveInto: {
          ri!actionButton_txt,
          a!save(
            ri!cdtForms[ri!FormIndex].LastModifiedDate,
            now()
          ),
          a!save(
            ri!cdtForms[ri!FormIndex].LastModifiedBy,
            loggedInUser()
          ),
          a!save(
            ri!componentIndex,
            1
          ),
          a!save(
            local!componentIndex,
            1
          ),
          a!save(
            local!isChangeTheSequenceIndex,
            false()
          )
        }
      )
    ),
    #"SYSTEM_SYSRULES_formLayout"(
      label: if(
        ri!isDashboardView,
        {},
        ri!cdtFrmVersionCmpnt[ri!FormIndex].label
      ),
      firstColumnContents: {
        local!basicInfoSection,
        local!instructionSection,
        local!mainConfigSection
      },
      buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
        primaryButtons: {
          /*local!copyFormBtn,*/
          /*local!nextBtn,*/
          local!saveBtn,
          local!previewFromBtn
        },
        secondaryButtons: {
          local!exitBtn,
          if(
            local!isChangeTheSequenceIndex = true(),
            {},
            {
              local!addComponentBtn,
              local!deleteComponentBtn
            }
          )/*local!prevComponentBtn*/
          
        }
      )
    )
  )
)
