rule.definition:
load(
  local!ShowPreviousForm: false(),
  with(
    local!preserveformData_cdt: ri!formData_cdt,
   

    local!FormLevelComponentList: #"_a-0000daa9-76cf-8000-927a-010000010000_58492"(

