          local!FormLevelComponentList.id
        )
      )
    ),
    local!sectionComponentList: #"SYSTEM_SYSRULES_applyComponents"(
      function: #"_a-0000dca4-b707-8000-927a-010000010000_205542"(
        componentIndex: _,
        formData_cdt: ri!formData_cdt,
        readOnly: ri!readOnly
      ),
      array: if(
        or(
          fn!isnull(
            local!FormLevelComponentIndexList
          ),
          length(
            local!FormLevelComponentIndexList
          ) = 0
        ),
        {},
        local!FormLevelComponentIndexList
      )
    ),
    local!generateForm: "load(
    " 
    &
    joinarray(apply(#"59ce59b5-8263-421a-b827-c46d6ae85a83"(ri!formData_cdt.componentList,_), enumerate(count(ri!formData_cdt.componentList)) + 1), "," & char(
    10
     ))
   &
   ", 
   a!formLayout(
      label:" & 
      #"02c08bd6-0170-4be3-9118-a1102ebabc3a"(
      ri!formData_cdt.label
    ) & ",
    instructions:" & 
    #"02c08bd6-0170-4be3-9118-a1102ebabc3a"(
	ri!formData_cdt.instruction
    ) & ",
    firstColumnContents: {
    " & 
    if(
      or(
        fn!isnull(
          local!sectionComponentList
        ),
        length(
          local!sectionComponentList
        ) = 0
      ),
      {},
      joinarray(local!sectionComponentList, "," & char(10))
    ) & "
    }
    )
  )",
  "/******************  " & ri!formData_cdt.Label & "  ******************/" & char(10) & local!generateForm & char(10) & "/*******************************************/"
