rule.definition:
with(
  local!isInActive: (
    not(
      fn!isnull(
        ri!formData_cdt.componentList[ri!componentIndex].isInActive
      )
    )
  ),
  local!noParentExist: or(
    fn!isnull(
      ri!formData_cdt.componentList[ri!componentIndex].parentList
    ),
    count(
      ri!formData_cdt.componentList[ri!componentIndex].parentList
    ) = 0
  ),
  local!numberOfParents: if(
    local!noParentExist,
    0,
    count(
      ri!formData_cdt.componentList[ri!componentIndex].parentList
    )
  ),
  local!shouldDisplayList: if(
    local!noParentExist,
    {},
    fn!apply(
      #"_a-0000dca4-b707-8000-927a-010000010000_205684"(
        formData_cdt: ri!formData_cdt,
        componentIndex: ri!componentIndex,
        parentIndex: _
      ),
      1 + enumerate(
        local!numberOfParents
      )
    )
  ),
  local!numberOfParentsAllowDisplay: if(
    local!noParentExist,
    0,
    length(
      fn!where(
        local!shouldDisplayList
      )
    )
  ),
  local!shouldDisplay: if(
    local!isInActive,
    False,

    if(
      local!noParentExist,
      True,
      if(
        ri!formData_cdt.componentList[ri!componentIndex].parentCondition = #"_a-0000da6e-240f-8000-6ee0-010000010000_1233",
        (
          local!numberOfParentsAllowDisplay > 0
        ),
        (
          local!numberOfParentsAllowDisplay = local!numberOfParents
        )
      )
    )
  ),
  local!shouldDisplay
)
