rule.definition:
load(
  local!assignee:ri!arrgrpAssigneeGroup[ri!intIndex],
   with(
     #"SYSTEM_SYSRULES_gridRowLayout"(
        id: ri!intIndex,
        contents: {
          #"SYSTEM_SYSRULES_textField"(
            readOnly:true(),
            value: ri!arrFormName[ri!intIndex]
          ),
          #"SYSTEM_SYSRULES_textField"(
            readOnly:true(),
            value: ri!arrFormDescription[ri!intIndex]
          ),
          #"SYSTEM_SYSRULES_pickerFieldUsersAndGroups"(
            value: local!assignee,
            maxSelections:1,
            validations: if( and(isnull(ri!arrusrFormAssignee[ri!intIndex]), isnull(ri!arrgrpAssigneeGroup[ri!intIndex])), "Provide assignee name or group",""),
            saveInto: {
              local!assignee,
              if(
                count(group(local!assignee,"id"))=0,
                {

              a!save(ri!arrusrFormAssignee[ri!intIndex],save!value),
              a!save(
                ri!arrgrpAssigneeGroup[ri!intIndex],
                null
              )
                },
                {
                  a!save(ri!arrusrFormAssignee[ri!intIndex],null),
              a!save(
                ri!arrgrpAssigneeGroup[ri!intIndex],
                save!value
              )
                }
              )
            }
          ),
          /*a!pickerFieldGroups(*/
          /*  value: ri!arrgrpAssigneeGroup[ri!intIndex],*/
          /*  validations: if( and(isnull(ri!arrusrFormAssignee[ri!intIndex]), isnull(ri!arrgrpAssigneeGroup[ri!intIndex])), "Provide assignee name or group",""),*/
          /*  saveInto: */
          /*  {*/
          /*    ri!arrgrpAssigneeGroup[ri!intIndex],*/
          /*    a!save(*/
          /*      ri!arrusrFormAssignee[ri!intIndex],*/
          /*      null*/
          /*    )*/
          /*  }*/

          /*),*/
          #"SYSTEM_SYSRULES_dateTimeField"(
            label: "Form Deadline",
            validations: if(
              and(not(isnull(ri!arrdttimDeadLine[ri!intIndex])),ri!arrdttimDeadLine[ri!intIndex] <= now()),
              "Deadline must be a future date",
              ""
            ),
            value: ri!arrdttimDeadLine[ri!intIndex],
            saveInto: ri!arrdttimDeadLine[ri!intIndex]
          )
        }
 )
 )
 )
