rule.definition:
load(
  local!startDate: today()-7,
  with(
    local!DateSeries: if(isnull(local!startDate),
    null(),
    #"bcea8918-34af-4e88-bff8-1620e5ec2e1d"(
      local!startDate
    )
    ),
    local!TempAppData: if(isnull(local!DateSeries),
    null(),
    #"SYSTEM_SYSRULES_applyComponents"(
      function: #"c739dfcc-c4c7-4a0a-a46f-b7efb5e07b7f"(
        _
      ),
      array: local!DateSeries
    )
    ),
    local!AppData: if(isnull(local!TempAppData),
    {},
    reject(fn!isnull,reduce(fn!append,{},(local!TempAppData)))
    ),
    {
      #"SYSTEM_SYSRULES_dateField"(
        label: "Choose a Start Date",
        instructions:  "Please select a date before " & today(),
        value: local!startDate,
        required: true(),
        saveInto: 
        {
          a!save(
            local!startDate,
            if(
             save!value > today(), local!startDate, save!value
            )
          )
        },
        validations: {
          if(
            local!startDate > today(),
            "Please select a date before " & today(),
            {}
          )
        }
      ),
      if(isnull(local!DateSeries),
      {},
      #"SYSTEM_SYSRULES_columnChartField"(

        categories: text(
          local!DateSeries,
          "dddd (mm/dd/yyyy)"
        ),
        stacking : "NORMAL",
        series: if(
          and(not(isnull(local!startDate)),length(local!AppData)>0),
        with(
          local!arrintAppId : union(
            local!AppData.AppId,
            local!AppData.AppId
          ),
          local!Appdetails : #"3601113e-27f3-42a1-9701-d8fa99eadbf9"(local!arrintAppId).data,
          #"SYSTEM_SYSRULES_applyComponents"(
          #"091b6db0-a57d-4ced-8495-47db1b44e627"(
            local!AppData,
            local!DateSeries,
            _,
            _
          ),
          merge(
             local!Appdetails.Id,
              local!Appdetails.Name
          )
        )),{}
        )
      )
      )
    }
  )
)
